nsition: all 0.5s ease-in-out;margin: 50px;}@media screen and (min-width: 768px) {.links-nav ul li {  height: -webkit-fit-content;  height: -moz-fit-content;  height: fit-content;  padding: 0 10px;  margin: 0 10px;  font-size: 14px;}.links-nav ul li:last-of-type {  padding-right: 0;  margin-right: 0;}}.links-nav ul li.resume {color: #ff4c82;border: 2px solid #ff4c82;border-radius: 5px;padding: 6px 10px;}#introduction {background: linear-gradient(320deg, #121B74 25%, transparent 25%);background-position-x: 100vw;min-height: 100vh;transition: background-position-x 1s ease-in-out;min-height: 100vh;background-repeat: no-repeat;pointer-events: all;}#introduction.moved-in {background-position-x: 0;}@media screen and (min-width: 608px) {#introduction {  background: linear-gradient(340deg, #121B74 30%, transparent 30%);  background-position-x: 100vw;  background-repeat: no-repeat;}}#introduction * {transition: 1s opacity ease-in-out, 1s transform ease-in-out;}#introduction h1, #introduction h2 {will-change: opacity;}#introduction h3 {margin-top: 1rem;}#introduction a {pointer-events: all;cursor: pointer;background: transparent;border: 2px solid #ff4c82;color: #ff4c82;border-radius: 5px;margin-top: 2rem;}#about-me {background: #121B74;min-height: 100vh;}.heading {font-size: clamp(40px, 8vw, 80px);line-height: 1;}section {padding: 40px 24px;z-index: 1;}@media (min-width: 468px) {section {  padding-left: 2rem;  padding-right: 2rem;}}@media (min-width: 1024px) {section {  padding-left: 15rem;  padding-right: 15rem;}}@media (min-width: 1324px) {section {  padding-left: 20rem;  padding-right: 20rem;}}.section-heading {font-size: clamp(30px, 4vw, 40px);font-weight: 700;color: #ff4c82;}section p {line-height: 1.5;font-size: 18px;margin-top: 2rem;}@media screen and (min-width: 481px) {section p {  width: 50%;  font-size: 14px;}}#about-me * {transition: 1s opacity ease-in-out, 1s transform ease-in-out;}#about-me * ul {display: grid;grid-template-columns: 1fr 1fr;margin-top: 2rem;}#ab